INSERT INTO table_learning_module (name, description, in_maintenance) VALUES ('Gramática Básica', 'Módulo para introduzir conceitos básicos de gramática.', false), ('Vocabulário Avançado', 'Módulo focado em ampliar o vocabulário dos estudantes.', false);
INSERT INTO table_difficulty_level (name, difficulty_level, description, learning_module_id) VALUES ('Nível A1', '1', 'Iniciante, foco em gramática e frases simples.', 1), ('Nível A2', '2', 'Elementar, desenvolvimento de frases mais complexas.', 1), ('Nível B1', '3', 'Intermediário, gramática mais avançada e leitura.', 2), ('Nível B2', '4', 'Intermediário superior, maior foco em produção escrita.', 2);
INSERT INTO table_topic (name, description, module_id) VALUES ('Introdução à Gramática', 'Este tópico aborda os fundamentos da gramática, incluindo partes do discurso, estrutura de frases e regras básicas de pontuação. A compreensão desses conceitos é essencial para a formação de frases corretas e para a comunicação clara e eficaz.', 1), ('Construção de Frases', 'Neste tópico, os alunos aprenderão a construir frases simples e compostas, utilizando corretamente os verbos, substantivos e adjetivos. Ser capaz de formar frases claras e coesas é crucial para a escrita e fala em qualquer idioma.', 1), ('Vocabulário do Dia a Dia', 'Este tópico explora o vocabulário usado em situações cotidianas, como fazer compras, ir ao médico e interagir socialmente. Os alunos aprenderão palavras e frases úteis que facilitarão a comunicação no dia a dia.', 2), ('Expressões Comuns', 'Neste tópico, os alunos aprenderão expressões idiomáticas e coloquiais que são frequentemente usadas por falantes nativos. Compreender essas expressões ajudará os alunos a se sentirem mais confortáveis e naturais ao se comunicar.', 2);
INSERT INTO table_content (audio_url, imagem_url, text) VALUES (NULL, NULL, 'Hello! My name is Sarah, and I am 20 years old. I live in a small house with my family. I wake up at 7:00 AM every day. I brush my teeth and wash my face. After that, I go to the kitchen to eat breakfast. I like to eat bread with butter and drink coffee. My mother likes to drink tea, but my father drinks orange juice. At 8:00 AM, I leave the house and go to work. I work at a bookstore in the city. The bookstore is small, but it is very nice. I work from 9:00 AM to 5:00 PM. I like my job because I love books. I help people find the books they want, and sometimes I read books during my break. I have lunch at 12:00 PM. I usually eat a sandwich and an apple. After lunch, I go back to work. At 5:00 PM, I finish work and go home. When I get home, I watch TV for a little while or read a book. I also help my mother cook dinner. We eat dinner at 7:00 PM. We like to eat rice, chicken, and vegetables. After dinner, I clean the kitchen and then take a shower. Sometimes, I talk with my friends on the phone or on the computer. I go to bed at 10:00 PM because I need to wake up early for work. On the weekend, I do different things. Sometimes I go to the park with my friends, or I visit my grandmother. I like the weekend because I have more time to relax. This is my daily routine. What about you? What do you do every day?'), (NULL, NULL, 'Cada manhã, John acorda cedo e se prepara para o dia. Ele toma um café da manhã simples, geralmente composto de pão, manteiga e frutas. Depois do café, ele se veste rapidamente e se despede da família antes de sair. Ele vai ao mercado local, que fica a poucos minutos de sua casa, para comprar pães frescos e vegetais. John gosta de conversar com os vendedores e escolher os melhores produtos, sempre preferindo os alimentos orgânicos. Depois de fazer suas compras, ele retorna para casa e começa a planejar suas atividades do dia. '), (NULL, NULL, 'No seu tempo livre, John gosta de ler livros de mistério e ficção. Ele se senta em sua cadeira favorita, que fica perto da janela, e se perde nas histórias intrigantes que explora. A leitura é uma forma de relaxamento para ele e uma maneira de aprender novas palavras e expressões. Em sua estante, ele possui uma coleção de livros clássicos e contemporâneos, e frequentemente troca livros com seus amigos. Essa prática não apenas o ajuda a ampliar seu vocabulário, mas também fortalece suas amizades. '), (NULL, NULL, 'Às vezes, John e sua família vão ao parque nos fins de semana. Eles fazem piqueniques e jogam bola. O parque é um lugar agradável, com muitas árvores, um lago e áreas para caminhadas. Durante os piqueniques, eles costumam levar sanduíches, sucos e frutas frescas. John sempre se certifica de que a comida seja saudável e saborosa. As crianças adoram correr e brincar no parque, enquanto os adultos se sentam e conversam sobre suas semanas. É um momento especial em que todos podem relaxar e aproveitar a companhia uns dos outros. '), (NULL, NULL, 'Além disso, John está aprendendo uma nova língua, o inglês. Ele se dedica a estudar gramática e vocabulário todos os dias, assistindo a vídeos, lendo livros e praticando com amigos. John acredita que a prática constante é a chave para a fluência. Ele utiliza aplicativos de aprendizado de idiomas e participa de grupos de conversação online. Além disso, ele tem um caderno onde anota novas palavras e expressões que aprende. Ele sonha em viajar para países de língua inglesa e poder se comunicar com os nativos. '), (NULL, NULL, 'Em sua jornada de aprendizado, John se deparou com várias dificuldades. Às vezes, ele sente que seu vocabulário não é suficiente para expressar suas ideias com clareza. Para superar isso, ele se propõe a ler mais textos em inglês, desde artigos de notícias até romances. Ele também faz anotações em seu caderno sobre as palavras novas que encontra. Com o tempo, ele começou a se sentir mais confiante e capaz de se comunicar em inglês, o que é uma grande conquista para ele. '), (NULL, NULL, 'John também gosta de praticar esportes. Ele se exercita regularmente, correndo no parque próximo à sua casa. A corrida é uma forma de manter sua saúde e energia. Além disso, ele frequentemente se junta a um grupo de corrida que se reúne às terças-feiras. Participar do grupo o ajuda a manter a motivação e a conhecer novas pessoas que compartilham o mesmo interesse por esportes. John acredita que o exercício físico é essencial para o bem-estar, tanto mental quanto físico. '), (NULL, NULL, 'Por último, John tem uma rotina noturna que ele aprecia muito. Após um dia cheio de atividades, ele gosta de relaxar antes de dormir. Ele faz isso tomando um banho quente e lendo um livro na cama. Às vezes, ele também ouve música suave ou medita para acalmar sua mente. Essa rotina ajuda a preparar seu corpo para uma boa noite de sono. Ele valoriza esses momentos de tranquilidade, pois são essenciais para seu descanso e recuperação para o dia seguinte.');
INSERT INTO table_lesson (title, status, available, content_id, difficulty_level_id, description) VALUES ('Apresentação Pessoal', 'completo', true, 1, 1, 'Nesta lição, os alunos aprenderão a se apresentar, incluindo informações básicas como nome, idade e rotina diária.'), ('Rotina Diária', 'em progresso', true, 2, 1, 'Os alunos irão explorar as atividades diárias, descrevendo ações rotineiras e seus horários.'), ('Hobbies e Interesses', 'não iniciado', true, 3, 1, 'Esta lição foca nos hobbies e interesses dos alunos, como leitura, esportes e outras atividades de lazer.'), ('Atividades em Família', 'não iniciado', true, 4, 1, 'Os alunos aprenderão a descrever atividades realizadas com a família, como piqueniques, passeios e momentos de interação.'), ('Aprendendo uma Nova Língua', 'não iniciado', true, 5, 1, 'Esta lição aborda o aprendizado de uma nova língua, destacando métodos, desafios e a importância da prática constante.'), ('Vida Saudável', 'não iniciado', true, 6, 1, 'Os alunos irão aprender sobre hábitos saudáveis, incluindo práticas esportivas e escolhas alimentares.'), ('Momentos de Relaxamento', 'não iniciado', true, 7, 1, 'Os alunos explorarão maneiras de relaxar após um dia cheio, como leitura, meditação e momentos tranquilos em casa.');
INSERT INTO table_question (number, question_text, content_id) VALUES (1, 'Qual é o nome da pessoa apresentada no texto?', 1), (2, 'O que a pessoa faz todas as manhãs?', 2), (3, 'Com quem John vive?', 1), (4, 'Qual é o nome do gato de John?', 1), (5, 'O que John gosta de fazer no seu tempo livre?', 3), (6, 'O que a família de John faz no parque?', 4), (7, 'O que John está aprendendo atualmente?', 5), (8, 'Quais dificuldades John enfrenta em seu aprendizado?', 6), (9, 'Quais esportes John pratica?', 7), (10, 'Qual é a rotina noturna de John?', 7);
